mapper.select:


SELECT MAX(order_no)
FROM replyboard
WHERE group_no = #{groupNo}

mapper.update:


			UPDATE replyboard
			SET title = #{title},
   				content = #{content}
			WHERE no = #{no}



UPDATE replyboard
SET del = 'true'
WHERE no = #{no}



UPDATE replyboard
SET hit= hit+1
WHERE no = #{no}



UPDATE replyboard
SET order_no = order_no+1
WHERE group_no = #{groupNo}
and order_no > #{orderNo}
